<div class="example-container" [class.example-is-mobile]="mobileQuery.matches">
  <mat-toolbar color="primary" class="example-toolbar">
    <button mat-icon-button (click)="snav.toggle()"><mat-icon>menu</mat-icon></button>
    <h1 class="example-app-name">Responsive App</h1>
  </mat-toolbar>

  <mat-sidenav-container class="example-sidenav-container"
                         [style.marginTop.px]="mobileQuery.matches ? 56 : 0">
    <mat-sidenav #snav [mode]="mobileQuery.matches ? 'over' : 'side'"
                 [fixedInViewport]="mobileQuery.matches" fixedTopGap="56">
      <mat-nav-list>
        <mat-list-item role="listitem" routerLink="login" [routerLinkActive]="'active'">Login</mat-list-item>
        <mat-list-item role="listitem" routerLink="privacy" [routerLinkActive]="'active'">Privacy</mat-list-item>
        <mat-list-item role="listitem" routerLink="supporto" [routerLinkActive]="'active'">Supporto</mat-list-item>
        <mat-list-item role="listitem" routerLink="chi-siamo" [routerLinkActive]="'active'">Chi siamo</mat-list-item>
      </mat-nav-list>
    </mat-sidenav>

    <mat-sidenav-content>
      <router-outlet></router-outlet><!--Contenuto dinamico-->
    </mat-sidenav-content>
  </mat-sidenav-container>
</div>
